import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Cookies from "js-cookie";

function NavBar({ isLoggedIn, setIsLoggedIn }) {
  const [isLoggedOut, setIsLoggedOut] = useState(false);
  const [isLogClick, setIsLogClick] = useState(false);
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);

  // ... (other state variables)

  const navigate = useNavigate();

  useEffect(() => {
    const token = Cookies.get("Token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, [setIsLoggedIn]);

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!isMobileMenuOpen);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    Cookies.remove("Token");
    sessionStorage.removeItem('role');
    navigate("/");
  };

  return (
    <div>
      <nav className="bg-[#2B2A4C] shadow">
        <div className="container mx-auto px-6 py-3 md:flex md:justify-between md:items-center">
          <div className="flex justify-between items-center">
            <div>
              <Link
                to="/profile"
                className="text-white text-xl font-bold md:text-2xl hover:text-gray-700"
              >
                GEAR UP SHOP
              </Link>
            </div>
            <div className="flex md:hidden">
              <button
                type="button"
                className="text-gray-500 hover:text-gray-600 focus:outline-none focus:text-gray-600"
                aria-label="toggle menu"
                onClick={toggleMobileMenu}
              >
                <svg viewBox="0 0 24 24" className="h-6 w-6 fill-current">
                  <path
                    fillRule="evenodd"
                    d="M4 5h16a1 1 0 0 1 0 2H4a1 1 0 1 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2z"
                  />
                </svg>
              </button>
            </div>
          </div>
          <div className={`md:flex items-center ${isMobileMenuOpen ? 'block' : 'hidden'}`}>
            {/* Mobile menu items */}
            <Link to="/" className="block mt-4 text-lg text-white">Home</Link>
            <Link to="/shopall" className="block mt-4 text-lg text-white">Shop</Link>
            <Link to="/contactus" className="block mt-4 text-lg text-white">Contact</Link>
            <Link to="/about" className="block mt-4 text-lg text-white">About</Link>

            {/* ... (other mobile menu items) */}

            {isLoggedIn ? (
              <Link to="/profile" className="block mt-4 text-lg text-white">Profile</Link>
            ) : (
              <Link to="/login" className="block mt-4 text-lg text-white">Login</Link>
            )}

            {isLoggedIn && (
              <button
                onClick={handleLogout}
                className="block mt-4 text-lg text-white"
              >
                Logout
              </button>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
}

export default NavBar;
