import React from "react";
import { ElementsConsumer, CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import CardSection from "./CardSection";
import axios from "axios";
import Swal from "sweetalert2";
import { useCart } from "../context/CartContext";

function CheckoutForm({ stripe, elements }) {
  const { total, cartItems } = useCart();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: elements.getElement(CardElement),
    });

    if (error) {
      console.log(error.message);
      // Handle error appropriately
    } else {
      if (paymentMethod) {
        const { id } = paymentMethod;

        try {
          const response = await axios.post("http://localhost:5002/payment", {
            amount: total * 100, // assuming total is in dollars, convert it to cents
            id,
            customerInfo: {
              name: event.target.cus_name.value,
              email: event.target.cus_email.value,
              address: event.target.cus_address.value,
              city: event.target.cus_city.value,
              country: event.target.cus_country.value,
              zip: event.target.cus_zip.value,
            },
            cartItems,
          });

          if (response.data.success) {
            console.log(response.data);
            Swal.fire({
              title: "Payment",
              text: "Payment successful",
              icon: "success",
              confirmButtonText: "Got it!",
            });
          } else {
            console.log("Payment failed");
            // Handle failure appropriately
          }
        } catch (error) {
          console.error("Error making payment", error);
          // Handle error appropriately
        }
      } else {
        console.log("Payment method is undefined");
        // Handle undefined paymentMethod
      }
    }
  };

  return (
    <>
      <div className="leading-loose">
        <form onSubmit={handleSubmit} className="max-w-xl m-4 p-10 bg-white rounded shadow-xl">
          {/* Your customer information inputs */}
          <p className="mt-4 text-gray-800 font-medium">Payment information</p>
          <div className="">
            <label className="block text-sm text-gray-600" htmlFor="cus_name">
              Card
            </label>
            <CardSection />
          </div>
          <h2>{total}$</h2>
          <div className="mt-4">
            <button className="px-4 py-1 text-white font-light tracking-wider bg-gray-900 rounded" type="submit">
              Pay now
            </button>
          </div>
        </form>
      </div>
    </>
  );
}

export default CheckoutForm;
