import React from 'react';
import hero from 'C:/Users/Orange/Desktop\Gear-Up\Client\src\Assest\videoc.mp4';

function Herosection() {
  return (
    <>
      {/* forked from: https://codepen.io/cuonoj/pen/JjPmMaB */}
      <section className="relative h-screen flex flex-col items-center justify-center text-center text-white">
        <div className="video-docker absolute top-0 left-0 w-full h-full overflow-hidden">
          <video
            className="min-w-full min-h-full absolute object-cover"
            src={hero}
            type="video/mp4"
            controls // Add controls for the video player
          />
        </div>
        <div className="video-content space-y-2 z-10">
          <h1 className="font-light text-6xl">Full Hero Video</h1>
          <h3 className="font-light text-3xl">with TailwindCSS</h3>
        </div>
      </section>
      <style
        dangerouslySetInnerHTML={{
          __html: `
            .video-docker video {
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);
            }

            .video-docker::after {
                content: "";
                position: absolute;
                width: 100%;
                height: 100%;
                top: 0;
                left: 0;
                background: rgba(0, 0, 0, 0.6);
                z-index: 1;
            }
          `,
        }}
      />
    </>
  );
}

export default Herosection;
