import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Cookies from "js-cookie";

function NavBar({ isLoggedIn, setIsLoggedIn }) {
  const [isLoggedOut, setIsLoggedOut] = useState(false);
  const [isLogClick, setIsLogClick] = useState(false);
  const [isMobileMenuOpen, setMobileMenuOpen] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const token = Cookies.get("Token");
    if (token) {
      setIsLoggedIn(true);
    }
  }, [setIsLoggedIn]);

  const handleLoginClick = () => {
    setIsLogClick(!isLogClick);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    Cookies.remove("Token");
    sessionStorage.removeItem("role");
    navigate("/");
  };

  return (
    <div>
      <nav className="bg-[#2B2A4C] shadow">
        <div className="container mx-auto px-6 py-3 md:flex md:justify-between md:items-center">
          <div className="flex justify-between items-center">
            <div>
              <Link
                to="/profile"
                className="text-white text-xl font-bold md:text-2xl hover:text-gray-700"
              >
                GEAR UP SHOP
              </Link>
            </div>
            <div className="md:hidden">
              <button
                type="button"
                className="text-gray-500 hover:text-gray-600 focus:outline-none focus:text-gray-600"
                aria-label="toggle menu"
                onClick={() => setMobileMenuOpen(!isMobileMenuOpen)}
              >
                <svg viewBox="0 0 24 24" className="h-6 w-6 fill-current">
                  <path
                    fillRule="evenodd"
                    d="M4 5h16a1 1 0 0 1 0 2H4a1 1 0 1 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2zm0 6h16a1 1 0 0 1 0 2H4a1 1 0 0 1 0-2z"
                  />
                </svg>
              </button>
              {isMobileMenuOpen && (
                <div className="md:hidden flex flex-col mt-2 space-y-2 bg-[#2B2A4C]">
                  <Link to="/" className="text-white">
                    Home
                  </Link>
                  <Link to="/shopall" className="text-white">
                    Shop
                  </Link>
                  <Link to="/contactus" className="text-white">
                    Contact
                  </Link>
                  <Link to="/about" className="text-white">
                    About
                  </Link>
                </div>
              )}
            </div>
          </div>
          <div className="md:flex items-center">
            <div className="flex flex-col md:flex-row md:mx-6">
              {/* Add your existing menu items here */}
              <Link
                to="/"
                className="my-1 text-lg text-white font-medium hover:text-[#B31312] md:mx-4 md:my-0"
              >
                Home
              </Link>
              <Link
                to="/shopall"
                className="my-1 text-lg text-white font-medium hover:text-[#B31312] md:mx-4 md:my-0"
              >
                Shop
              </Link>
              <Link
                to="/contactus"
                className="my-1 text-lg text-white font-medium hover:text-[#B31312] md:mx-4 md:my-0"
              >
                Contact
              </Link>
              <Link
                className="my-1 text-lg text-white font-medium hover:text-[#B31312] md:mx-4 md:my-0"
                to="/about"
              >
                About
              </Link>
            </div>
            {/* ... (your existing code) */}
          </div>
        </div>
      </nav>
    </div>
  );
}

export default NavBar;
