import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductTable() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    fetchData();
  }, [currentPage]);

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:5002/getProductsWithImage?page=${currentPage}`);
      setProducts(response.data.products);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleSave = async (productId) => {
    try {
      // Implement your save logic here
      console.log('Save product with ID:', productId);
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };

  const handleDelete = async (productId) => {
    try {
      // Implement your delete logic here
      console.log('Delete product with ID:', productId);
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const generatePaginationLinks = () => {
    const links = [];
    for (let i = 1; i <= totalPages; i++) {
      links.push(
        <li key={i}>
          <button
            onClick={() => handlePageChange(i)}
            className={`flex items-center justify-center px-3 h-8 ${
              currentPage === i ? 'text-blue-600 border bg-blue-50' : 'text-gray-500 border hover:bg-gray-100 hover:text-gray-700'
            }`}
          >
            {i}
          </button>
        </li>
      );
    }
    return links;
  };

  return (
    <div>
      <div className="text-gray-900 bg-blue-200 overflow-hidden">
        <div className="p-4 flex">
          <h1 className="text-3xl">Products</h1>
        </div>
        <div className="px-3 py-4 flex justify-center overflow-hidden">
          <table className="w-full text-md bg-white shadow-md rounded mb-4 overflow-hidden">
            <tbody>
              <tr className="border-b">
                <th className="text-left p-3 px-5">Product Name</th>
                <th className="text-left p-3 px-5">Price</th>
                <th className="text-left p-3 px-5">Category</th>
                <th>Action</th>
              </tr>
              {Array.isArray(products) && products.length > 0 ? (
                products.map((product) => (
                  <tr key={product._id} className="border-b hover:bg-orange-100 bg-gray-100">
                    <td className="p-3 px-5">{product.name}</td>
                    <td className="p-3 px-5">{product.price}</td>
                    <td className="p-3 px-5">{product.category}</td>
                    <td className="p-3 px-5 flex justify-end">
                      <button
                        type="button"
                        onClick={() => handleSave(product._id)}
                        className="mr-3 text-sm bg-blue-500 hover:bg-blue-700 text-white py-1 px-2 rounded focus:outline-none focus:shadow-outline"
                      >
                        Save
                      </button>
                      <button
                        type="button"
                        onClick={() => handleDelete(product._id)}
                        className="text-sm bg-red-500 hover:bg-red-700 text-white py-1 px-2 rounded focus:outline-none focus:shadow-outline"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="4">No products found</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        <div className="flex items-center justify-center">
          <nav aria-label="Page navigation example">
            <ul className="inline-flex -space-x-px text-sm">
              <li>
                <button
                  onClick={() => handlePageChange(currentPage - 1)}
                  disabled={currentPage === 1}
                  className="flex items-center justify-center px-3 h-8 ms-0 leading-tight text-gray-500 bg-white border border-e-0 border-gray-300 rounded-s-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                >
                  Previous
                </button>
              </li>
              {generatePaginationLinks()}
              <li>
                <button
                  onClick={() => handlePageChange(currentPage + 1)}
                  disabled={currentPage === totalPages}
                  className="flex items-center justify-center px-3 h-8 leading-tight text-gray-500 bg-white border border-gray-300 rounded-e-lg hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white"
                >
                  Next
                </button>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </div>
  );
}

export default ProductTable;
