import React, { useState } from "react";
import { Link } from "react-router-dom";

function Sidebar() {
  const [isSidebarOpen, setSidebarOpen] = useState(true);

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  return (
    <div>
      <>
        <div className="bg-white shadow">
          <div className="container mx-auto">
            <div className="flex justify-between items-center py-4 px-2">
              <h1 className="text-xl font-semibold">Animated Drawer</h1>
              <button
                className="text-gray-500 hover:text-gray-600"
                id="open-sidebar"
                onClick={toggleSidebar}
              >
                <svg
                  className="w-6 h-6"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  {isSidebarOpen ? (
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M6 18L18 6M6 6l12 12"
                    />
                  ) : (
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M4 6h16M4 12h16M4 18h16"
                    />
                  )}
                </svg>
              </button>
            </div>
          </div>
        </div>
        <div
          className={`bg-white border-b border-gray-300 flex flex-col absolute z-40 left-0 top-0 lg:static lg:left-auto lg:top-auto lg:translate-x-0 h-screen overflow-y-scroll lg:overflow-y-auto no-scrollbar lg:sidebar-expanded:${
            isSidebarOpen ? "w-64" : "!w-64"
          } shrink-0 p-4 transition-all duration-200 ease-in-out`}
        >
          <aside className="w-64" aria-label="Sidebar ">
            <div className="px-3 py-4 overflow-y-auto rounded bg-gray-200 dark:bg-gray-800">
              <ul className="space-y-2">
                {/* ... (rest of your sidebar links) */}
              </ul>
            </div>
          </aside>
        </div>
      </>
    </div>
  );
}

export default Sidebar;
