import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductTable() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(2); // Adjust the number of products per page as needed

  useEffect(() => {
    fetchData();
  }, [currentPage, pageSize]);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        `http://localhost:5002/getProductsWithImage?page=${currentPage}&pageSize=${pageSize}`
      );
      setProducts(response.data);
    } catch (error) {
      console.error('حدث خطأ في جلب البيانات:', error);
    }
  };

  const handleSave = async (productId) => {
    try {
      await axios.put(`http://localhost:5002/getProductsWithImage/${productId}`, /* بيانات التحديث */);
      fetchData();
    } catch (error) {
      console.error('حدث خطأ في عملية الحفظ:', error);
    }
  };

  const handleDelete = async (productId) => {
    try {
      await axios.delete(`http://localhost:5002/delete/${productId}`);
      fetchData();
    } catch (error) {
      console.error('حدث خطأ في عملية الحذف:', error);
    }
  };

  const totalPages = Math.ceil(products.length / pageSize);

  const renderPageNumbers = () => {
    const pages = [];
    for (let i = 1; i <= totalPages; i++) {
      pages.push(
        <li key={i}>
          <a
            href="#"
            className={`flex items-center justify-center px-3 h-8 leading-tight text-gray-500 bg-white border border-gray-300 ${
              currentPage === i
                ? 'bg-blue-50 text-blue-600 hover:bg-blue-100 hover:text-blue-700'
                : 'hover:bg-gray-100 hover:text-gray-700 dark:border-gray-700 dark:text-white'
            }`}
            onClick={() => setCurrentPage(i)}
          >
            {i}
          </a>
        </li>
      );
    }
    return pages;
  };

  return (
    <div>
      <div className="text-gray-900 bg-blue-200   overflow-hidden">
        <div className="p-4 flex ">
          <h1 className="text-3xl">Products</h1>
        </div>
        <div className="px-3 py-4 flex justify-center overflow-hidden">
          <table className=" w-full text-md bg-white shadow-md rounded mb-4 overflow-hidden">
            <tbody>
              <tr className="border-b">
                <th className="text-left p-3 px-5">Product Name</th>
                <th className="text-left p-3 px-5">Price</th>
                <th className="text-left p-3 px-5">Category</th>
                <th />
              </tr>
              {Array.isArray(products) && products.length > 0 ? (
                products.map((product) => (
                  <tr key={product._id} className="border-b hover:bg-orange-100 bg-gray-100">
                    <td className="p-3 px-5">
                      <input
                        type="text"
                        value={product.name}
                        className="bg-transparent"
                        readOnly
                      />
                    </td>
                    <td className="p-3 px-5">
                      <input
                        type="text"
                        value={product.price}
                        className="bg-transparent"
                        readOnly
                      />
                    </td>
                    <td className="p-3 px-5">
                      <input
                        type="text"
                        value={product.category}
                        className="bg-transparent"
                        readOnly
                      />
                    </td>
                    <td className="p-3 px-5 flex justify-end">
                      <button
                        type="button"
                        onClick={() => handleSave(product._id)}
                        className="mr-3 text-sm bg-blue-500 hover:bg-blue-700 text-white py-1 px-2 rounded focus:outline-none focus:shadow-outline"
                      >
                        Save
                      </button>
                      <button
                        type="button"
                        onClick={() => handleDelete(product._id)}
                        className="text-sm bg-red-500 hover:bg-red-700 text-white py-1 px-2 rounded focus:outline-none focus:shadow-outline"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="4">No products found</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        <div className="flex items-center justify-center">
          <nav aria-label="Page navigation example">
            <ul className="inline-flex -space-x-px text-sm">
              <li>
                <a
                  href="#"
                  className={`flex items-center justify-center px-3 h-8 ${
                    currentPage === 1
                      ? 'text-gray-300 cursor-not-allowed'
                      : 'text-gray-500 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white'
                  }`}
                  onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                >
                  Previous
                </a>
              </li>
              {renderPageNumbers()}
              <li>
                <a
                  href="#"
                  className={`flex items-center justify-center px-3 h-8 ${
                    currentPage === totalPages
                      ? 'text-gray-300 cursor-not-allowed'
                      : 'text-gray-500 hover:bg-gray-100 hover:text-gray-700 dark:bg-gray-800 dark:border-gray-700 dark:text-gray-400 dark:hover:bg-gray-700 dark:hover:text-white'
                  }`}
                  onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                >
                  Next
                </a>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </div>
  );
}

export default ProductTable;
