import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Statas() {
  const [usersCount, setUsersCount] = useState(null);
  const [ordersCount, setOrdersCount] = useState(null);
  const [productsCount, setProductsCount] = useState(null);

  useEffect( async () => {
    // Fetch the number of users
 await    axios.get('http://localhost:5002/users')
      .then(response => setUsersCount(response.data)
      )
      .catch(error => console.error('Error fetching users count:', error));
      console.log(usersCount.length);

    // Fetch the number of orders
    axios.get('your_orders_api_endpoint')
      .then(response => setOrdersCount(response.data))
      .catch(error => console.error('Error fetching orders count:', error));

    // Fetch the number of available products
    axios.get('your_products_api_endpoint')
      .then(response => setProductsCount(response.data))
      .catch(error => console.error('Error fetching products count:', error));
  }, []); // The empty dependency array ensures that this effect runs once when the component mounts

  return (
    <div>
      <div className="m-6">
        <div className="flex flex-wrap -mx-6">
          {/* Display the number of users */}
          <div className="w-full px-6 sm:w-1/2 xl:w-1/3">
            <div className="flex items-center px-5 py-6 shadow-sm rounded-md bg-slate-100">
              <div className="mx-5">
                <h4 className="text-2xl font-semibold text-gray-700">
                  {usersCount !== null ? usersCount : 'Loading...'}
                </h4>
                <div className="text-gray-500">Users</div>
              </div>
            </div>
          </div>

          {/* Display the number of orders */}
          <div className="w-full mt-6 px-6 sm:w-1/2 xl:w-1/3 sm:mt-0">
            <div className="flex items-center px-5 py-6 shadow-sm rounded-md bg-slate-100">
              <div className="mx-5">
                <h4 className="text-2xl font-semibold text-gray-700">
                  {ordersCount !== null ? ordersCount : 'Loading...'}
                </h4>
                <div className="text-gray-500">Total Orders</div>
              </div>
            </div>
          </div>

          {/* Display the number of available products */}
          <div className="w-full mt-6 px-6 sm:w-1/2 xl:w-1/3 xl:mt-0">
            <div className="flex items-center px-5 py-6 shadow-sm rounded-md bg-slate-100">
              <div className="mx-5">
                <h4 className="text-2xl font-semibold text-gray-700">
                  {productsCount !== null ? productsCount : 'Loading...'}
                </h4>
                <div className="text-gray-500">Available Products</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Statas;
