import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Cookies from 'js-cookie';

function NavBar({ isLoggedIn, setIsLoggedIn }) {
  const [searchQuery, setSearchQuery] = useState('');
  const [dataSource, setDataSource] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch your data (replace 'your-api-endpoint' with the actual endpoint)
    fetch('your-api-endpoint')
      .then(response => response.json())
      .then(data => setDataSource(data.users)) // Assuming 'users' is the array you want to filter
      .catch(error => console.error('Error fetching data:', error));

    const token = Cookies.get('Token');
    if (token) {
      setIsLoggedIn(true);
    }
  }, [setIsLoggedIn]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    console.log('Searching for:', searchQuery);

    // Implement your search logic here
    const filteredData = dataSource.filter((record) => {
      return (
        record.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        record.lastName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        record.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
        record.phone.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (record.address &&
          (record.address.address.toLowerCase().includes(searchQuery.toLowerCase()) ||
            record.address.city.toLowerCase().includes(searchQuery.toLowerCase())))
      );
    });

    // Do something with the filtered data, e.g., update the component state or navigate to a search results page
    console.log('Filtered Data:', filteredData);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    Cookies.remove('Token');
    Cookies.remove('user_id');
    navigate('/');
  };

  return (
    <div>
      <>
        <nav className="bg-white shadow">
          <div className="container mx-auto px-6 py-3 md:flex md:justify-between md:items-center">
            {/* ... (Your existing code) ... */}

            {/* Search Form */}
            <form onSubmit={handleSearchSubmit}>
              <div className="pt-2 relative mx-auto text-gray-600">
                <input
                  className="border-2 border-gray-300 bg-white h-10 px-5 pr-16 rounded-lg text-sm focus:outline-none"
                  type="search"
                  name="search"
                  placeholder="Search"
                  value={searchQuery}
                  onChange={handleSearchChange}
                />
                <button type="submit" className="absolute right-0 top-0 mt-5 mr-4">
                  <svg
                    className="text-gray-600 h-4 w-4 fill-current"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 56.966 56.966"
                  >
                    <path d="M55.146,51.887L41.588,37.786c3.486-4.144,5.396-9.358,5.396-14.786c0-12.682-10.318-23-23-23s-23,10.318-23,23  s10.318,23,23,23c4.761,0,9.298-1.436,13.177-4.162l13.661,14.208c0.571,0.593,1.339,0.92,2.162,0.92  c0.779,0,1.518-0.297,2.079-0.837C56.255,54.982,56.293,53.08,55.146,51.887z M23.984,6c9.374,0,17,7.626,17,17s-7.626,17-17,17  s-17-7.626-17-17S14.61,6,23.984,6z" />
                  </svg>
                </button>
              </div>
            </form>

            {/* ... (Your existing code) ... */}
          </div>
        </nav>
      </>
    </div>
  );
}

export default NavBar;
