import React, { useState } from 'react';
import axios from 'axios';

function AddProduct() {
  const [productData, setProductData] = useState({
    productName: '',
    image: '',
    price: '',
    description: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProductData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Make a POST request to your endpoint
      const response = await axios.post('YOUR_API_ENDPOINT', productData);

      // Handle the response, e.g., show a success message
      console.log('Product added successfully:', response.data);

      // Clear the form after successful submission
      setProductData({
        productName: '',
        image: '',
        price: '',
        description: '',
      });
    } catch (error) {
      // Handle errors, e.g., show an error message
      console.error('Error adding product:', error);
    }
  };

  return (
    <div>
      <section className="items-center">
        <h1 className="bg-clip-text text-transparent bg-gradient-to-r from-indigo-500 to-teal-500 text-5xl font-black">
          Add Product
        </h1>
        <form className="max-w-xl items-center" onSubmit={handleSubmit}>
          {/* ... (unchanged code for input fields) */}
          
          <button
            type="submit"
            className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
          >
            ADD product
          </button>
        </form>
      </section>
    </div>
  );
}

export default AddProduct;
