// ProductDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
axios.defaults.headers.common['Authorization'] = `${localStorage.getItem('Token')}`;

// axios.defaults.headers.common['Authorization'] = `${localStorage.getItem('Token')}`;
function ProductDetails() {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:5002/details/${productId}`);
        console.log('Fetched product details:', response.data);
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product details:', error);
      }
    };

    fetchProduct();
  }, [productId]);

//   const handleAddToCart = async () => {

//     try {
//       await axios.post(`http://localhost:5002/add-to-cart/${productId}`, {
//         // productId: product.productId,
//         // Add other product details you want to store in the cart
//       });

//  // Show an alert when the product is successfully added to the cart
//       window.alert('Product added to cart successfully!');

//       console.log('Product added to cart successfully!');
//       // You may want to update the UI to reflect the change, e.g., show a confirmation message
//     } catch (error) {
//       console.error('Error adding product to cart:', error);
//     }
//   };
const addToCart = () => {
  // Create an object with the product data
  const productData = {
    "product_id": product.productId,

    "user_id" : product.user_id,
    // Add other properties as needed
  };

  // Make a POST request to your shopping cart endpoint
  axios
    .post("http://localhost:5002/add-to-cart/", productData)
    .then((response) => {
      console.log("Product added to cart:", response.data);
    })
    .catch((error) => {
      console.error("Error adding product to cart: ", error);
    });
};

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="bg-gray-100 dark:bg-gray-800 py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex flex-col md:flex-row -mx-4">
          <div className="md:flex-1 px-4">
            <div className="h-[460px] rounded-lg bg-gray-300 dark:bg-gray-700 mb-4">
              <img
                className="w-full h-full object-cover"
                src={product.image[0].imageUrl}
                alt={product.name}
              />
            </div>
            <div className="flex -mx-2 mb-4">
              <div className="w-1/2 px-2">
                <button
                  onClick={addToCart}
                  className="w-full bg-gray-900 dark:bg-gray-600 text-white py-2 px-4 rounded-full font-bold hover:bg-gray-800 dark:hover:bg-gray-700"
                >
                  Add to Cart
                </button>
              </div>
              <div className="w-1/2 px-2">
                <button
                  className="w-full bg-gray-200 dark:bg-gray-700 text-gray-800 dark:text-white py-2 px-4 rounded-full font-bold hover:bg-gray-300 dark:hover:bg-gray-600"
                >
                  Add to Wishlist
                </button>
              </div>
            </div>
          </div>
          <div className="md:flex-1 px-4">
            <h2 className="text-2xl font-bold text-gray-800 dark:text-white mb-2">
              {product.title}
            </h2>
            <p className="text-gray-600 dark:text-gray-300 text-sm mb-4">
              {product.description}
            </p>
            <div className="flex mb-4">
              <div className="mr-4">
                <span className="font-bold text-gray-700 dark:text-gray-300">
                  Price:
                </span>
                <span className="text-gray-600 dark:text-gray-300">${product.price}</span>
              </div>
              <div>
                <span className="font-bold text-gray-700 dark:text-gray-300">
                  Availability:
                </span>
                <span className="text-gray-600 dark:text-gray-300">{product.availability}</span>
              </div>
            </div>
            {/* <div>
              <span className="font-bold text-gray-700 dark:text-gray-300">
                Product Description:
              </span>
              <p className="text-gray-600 dark:text-gray-300 text-sm mt-2">
                {product.longDescription}
              </p>
            </div> */}
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProductDetails;
